    def on_tree_item_clicked(self, item):
            # Kim tra top-level item u tin
            if item == self.treeWidget.topLevelItem(0):
                    self.stackedWidget.setCurrentIndex(5)
                    self.stackedWidget_2.setCurrentIndex(0)

            # Kim tra top-level item th hai
            elif item == self.treeWidget.topLevelItem(1):
                    self.stackedWidget.setCurrentIndex(6)
                    self.stackedWidget_2.setCurrentIndex(0)

            # Kim tra cc child items ca top-level item th hai
            elif item in [self.treeWidget.topLevelItem(1).child(0),
                          self.treeWidget.topLevelItem(1).child(1),
                          self.treeWidget.topLevelItem(1).child(2),
                          self.treeWidget.topLevelItem(1).child(3)]:
                    # S dng iu kin  xc nh child item v thit lp ch s tng ng ca stackedWidget_2
                    index = 0
                    if item == self.treeWidget.topLevelItem(1).child(0):
                            index = 0
                    elif item == self.treeWidget.topLevelItem(1).child(1):
                            index = 1
                    elif item == self.treeWidget.topLevelItem(1).child(2):
                            index = 2
                    else:
                            index = 3
                    self.stackedWidget.setCurrentIndex(6)
                    self.stackedWidget_2.setCurrentIndex(index)

            # Kim tra top-level item th ba
            elif item == self.treeWidget.topLevelItem(2):
                    self.stackedWidget.setCurrentIndex(0)
                    self.stackedWidget_4.setCurrentIndex(0)

